import React from 'react'
// import Button from '@material-ui/core/Button';
// import Menu from '@material-ui/core/Menu';
// import MenuItem from '@material-ui/core/MenuItem';
import './home.less'

const index = () => {
    const [btnshow, setBtnshow] = React.useState(false);
    const setShow = (e, a) => {
        console.log(e)
        // 阻止事件冒泡，（阻止这个合成事件，往document上冒泡，因此不会触发click方法）
        e.nativeEvent.stopImmediatePropagation()
        // 阻止合成事件间的冒泡，不会往最外层的div的test方法冒了，如果不加这句代码，就会冒泡至外层div，执行test方法。      
        e.stopPropagation()
        setBtnshow(a)
    }
    return (
        <div>
            <div className='home-box' onClick={() => setShow(false)} >
                <div className='home-search'>

                    <p>Fusion Blockchain Explorer</p>
                    <div>
                        <div>
                            <button className='home-btn' onClick={(e) => setShow(e, true)}>All Filters</button>
                        </div>
                        <div className={btnshow ? 'home-show' : 'home-onshow'}>
                            sdsdsd
                                {`${btnshow}`}
                        </div>
                    </div>
                </div>
            </div>
        </div >
    )
}


export default index
