import React, { useState, useEffect } from "react";
// import { getAssets } from "../../api";
import Nav from "../nav";
import axios from 'axios';
export default function assetsList() {
    const [data, setData] = useState(null);
    useEffect(() => {
        const query = window.location.pathname.substring(8);
        console.log(query)
        const fetchData = async () => {
            const result = await axios.get(`https://api.fsn365.com/assets/${query}`)
            setData(result.data.data)
        };
        fetchData();
    }, []);
    return (
        <div>
            <Nav />
            {data && (
                <div className="page-main">
                    <header>
                        <h3>
                            {console.log(data)}
                            Assets <small>{`#${data.symbol}`}</small>
                        </h3>
                    </header>
                </div>
            )}
            {data && (
                <div>
                    <p>Asset Name:{data.name}</p>
                    <p>Asset Symbol:{data.symbol}</p>
                    <p>Asset ID:{data.id}</p>
                    <p>Decimals:{data.decimals}</p>
                    <p>Supply:{data.total}</p>
                    <p>Changeable Supply:NO</p>
                    <p>Issuer:{data.issuer}</p>
                    <p>Description:{data.description}</p>
                    <p>Issue Height:{data.issueBk}</p>
                    <p>Issue Time:{data.time}</p>
                    <p>Verified:NO</p>
                </div>
            )}
        </div>
    )
}
