import React, { useState, useEffect } from "react";
import { getBlocksHeight } from "../../api";
import Nav from "../nav";

export default function BlockPage() {
  const [data, setData] = useState(null);

  useEffect(() => {
    const query = window.location.pathname.substring(8);
    const fetchData = async () => {
      const res = await getBlocksHeight(query);
      setData(res.data);
    };
    fetchData();
  }, []);

  console.log(data);

  return (
    <div>
      <Nav />

      {data && (
        <div className="page-main">
          <div className="main">
            <header>
              <h3>
                Block <small>{`#${data.height}`}</small>
              </h3>
            </header>
            <div className="blocks-list">
              <div className="blocks-item">
                <p>{`Height：${data.height}`}</p>
                <hr />
              </div>
              <div className="blocks-item">
                <p>{`Age：${data.age}`}</p>
                <hr />
              </div>
              <div className="blocks-item">
                <p>{`Transactions：${data.height}`}</p>
                <hr />
              </div>
              <div className="blocks-item">
                <p>{`Block Miner：${data.height}`}</p>
                <hr />
              </div>
              <div className="blocks-item">
                <p>{`Block Rewards：${data.height}`}</p>
                <hr />
              </div>
              <div className="blocks-item">
                <p>{`Difficulty：${data.height}`}</p>
                <hr />
              </div>
              <div className="blocks-item">
                <p>{`Total Difficulty：${data.height}`}</p>
                <hr />
              </div>
            </div>
          </div>
        </div>
      )}

      {/* {data && (
        <div>
          <p>{data.timestamp}</p>
          <p>{data.miner}</p>
          <p>{data.hash}</p>
          <p>{data.difficulty}</p>
          <p>{data.gasLimit}</p>
        </div>
      )} */}
    </div>
  );
}
